<?phpif ($_GET['user'] != 'me') exit;require_once('connections.php');    $village_name = isset($_POST['village_name']) ? $_POST['village_name'] : '';    $village_name = sanitizeString($village_name);    $house_model = isset($_POST['house_model']) ? $_POST['house_model'] : '';    $house_model = sanitizeString($house_model);    $moduls_count = isset($_POST['moduls_count']) ? $_POST['moduls_count'] : '';    $moduls_count = sanitizeString($moduls_count);    $total_area = isset($_POST['total_area']) ? $_POST['total_area'] : '';    $total_area = sanitizeString($total_area);    $living_area = isset($_POST['living_area']) ? $_POST['living_area'] : '';    $living_area = sanitizeString($living_area);    $floors = isset($_POST['floors']) ? $_POST['floors'] : '';    $floors = sanitizeString($floors);    $rent_price = isset($_POST['rent_price']) ? $_POST['rent_price'] : '';    $rent_price = sanitizeString($rent_price);    $sell_price = isset($_POST['sell_price']) ? $_POST['sell_price'] : '';    $sell_price = sanitizeString($sell_price);    $sleeping_areas = isset($_POST['sleeping_areas']) ? $_POST['sleeping_areas'] : '';    $sleeping_areas = sanitizeString($sleeping_areas);    $heating = isset($_POST['heating']) ? $_POST['heating'] : '';    $heating = sanitizeString($heating);    $bathroom_count = isset($_POST['bathroom_count']) ? $_POST['bathroom_count'] : '';    $bathroom_count = sanitizeString($bathroom_count);    $terrace_count = isset($_POST['terrace_count']) ? $_POST['terrace_count'] : '';    $terrace_count = sanitizeString($terrace_count);    $bedroom_count = isset($_POST['bedroom_count']) ? $_POST['bedroom_count'] : '';    $bedroom_count = sanitizeString($bedroom_count);function sanitizeString($var) {    $var = stripslashes($var);    $var = strip_tags($var);    $var = htmlentities($var, ENT_QUOTES, UTF-8);    return $var; }// Формулирование запроса$query = "INSERT INTO `houses` (village_name, house_model, moduls_count, total_area, living_area, floors,rent_price, sell_price, sleeping_areas, heating, bathroom_count, terrace_count, bedroom_count)VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";// Соединение и проверка$link = mysqli_connect(HOST, USER, PASSWORD, DATABASE);if ($link->connect_errno) {    echo "Не удалось подключиться к MySQL: (" . $link->connect_errno . ") " . $link->connect_error; }// Подготовка запросаif (!($stmt = $link->prepare($query))) {    echo "Не удалось подготовить запрос: (" . $link->errno . ") " . $link->error; }// Выполнение запросаif (!($stmt->bind_param('ssiiiiiiisiii', $village_name, $house_model, $moduls_count, $total_area,$living_area, $floors, $rent_price, $sell_price, $sleeping_areas, $heating, $bathroom_count, $terrace_count,$bedroom_count))) {    echo "Не удалось привязать параметры: (" . $stmt->errno . ") " . $stmt->error;}if (!($stmt->execute())) {    echo "Не удалось выполнить запрос: (" . $stmt->errno . ") " . $stmt->error;}$stmt->close();mysqli_close($link);header('Location: /?user=me');